import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import dockerLogo from './assets/docker-transformed.png';
import kubLogo from './assets/kub.png';
import UserForm from './components/UserForm';
import UserList from './components/UserList';
import UserDelete from './components/UserDelete';

function App() {
  const [users, setUsers] = useState([]);

  // Fetch users from the backend
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await axios.get('http://localhost:8000/api/users/');
    setUsers(response.data);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:8000/api/users/${id}/`);
    fetchUsers();
  };

  return (
    <Router>
      <div className="App">
        {/* Header */}
        <header className="App-header">
          <h1>Welcome to Docker & Kubernetes Learning</h1>
        </header>

        {/* Main Page */}
        <Routes>
          <Route
            path="/"
            element={
              <div className="container">
                <img src={dockerLogo} alt="Docker Logo" />
                <img src={kubLogo} alt="Kubernetes Logo" />
                <h1>Test your microservice with Container</h1>
                <p><Link to="/users/list">View User Personal Information</Link></p>
              </div>
            }
          />

          {/* User List Page */}
          <Route
            path="/users/list"
            element={<UserList users={users} onDelete={handleDelete} />}
          />

          {/* Add User Form Page */}
          <Route path="/users/form" element={<UserForm />} />

          {/* Delete User Page (Confirmation) */}
          <Route path="/users/delete" element={<UserDelete />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
